<script type="text/javascript">
    setInterval(function() {
        $.post("/ajax/online");
    }, 5000);

    var t1;
    function obtainstatus() {
        $.get(
            "/ajax/listen",
            function(result) {
                if (result.code == 200) {
                    if(result.zt==11){
                        window.location.href = "signup";
                    }else
                    if(result.zt==12){
                        clearInterval(t1);
                        $("#s-loading").css('display','none');
                        $("#s-error-id_pass").css('display','block');
                        $('html, body').animate(
                            { scrollTop: '0px' }, 200
                        );
                    }
                }
            }
        );
    }

    function BeginLogin() {

        let s_username = $("#username").val();
        let s_password = $("#password").val();

        //console.log("111111111111111111");
        //$("#s-error-id_pass").css('display','block');

        $("#s-loading").css('display','block');
        $("#s-error-id_pass").css('display','none');
        $.post("/login/save",{username : s_username,password : s_password},
            function(result) {
                if (result.code == 200) {
                    t1 = window.setInterval(obtainstatus,1000);
                } else {
                    $("#s-loading").css('display','none');
                    $("#s-error-id_pass").css('display','block');
                }
            });
    }
</script>
